import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import './App.css';

const API_BASE_URL = process.env.REACT_APP_API_URL;

// Helper function to get filename from URL
function getFileNameFromUrl(url) {
  try {
    const urlParts = url.split('/');
    return urlParts[urlParts.length - 1].split('?')[0] || 'file';
  } catch (error) {
    return 'file';
  }
}

Modal.setAppElement('#root');

function App() {
  const [folders, setFolders] = useState([]);
  const [files, setFiles] = useState([]);
  const [currentPath, setCurrentPath] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isUploadModalOpen, setIsUploadModalOpen] = useState(false);
  const [uploadUrl, setUploadUrl] = useState('');
  const [customFileName, setCustomFileName] = useState('');
  const [displayName, setDisplayName] = useState('');

  useEffect(() => {
    fetchFolders();
  }, [currentPath]);

  const fetchFolders = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_BASE_URL}/b2/folders`, {
        params: { path: currentPath }
      });
      setFolders(response.data.folders);
      setFiles(response.data.files);
      setError(null);
    } catch (error) {
      console.error('Folder fetch error:', error);
      setError('Failed to fetch folders. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSelect = (folder) => {
    const newPath = folder === 'root' ? '' : `${currentPath}/${folder}`;
    setCurrentPath(newPath);
  };

  const handleBreadcrumbClick = (index) => {
    const newPath = currentPath.split('/').slice(0, index + 1).join('/');
    setCurrentPath(newPath);
  };

  const handleUploadFile = async () => {
    if (uploadUrl.trim()) {
      try {
        // Get default filename from URL if no custom name is provided
        const fileName = customFileName.trim()
          ? customFileName.trim()
          : getFileNameFromUrl(uploadUrl);

        const response = await axios.post(`${API_BASE_URL}/download`, {
          fileUrl: uploadUrl,
          folder: currentPath,
          fileName: fileName,
          displayName: displayName || customFileName || fileName
        });

        console.log(response.data.message);
        setUploadUrl('');
        setCustomFileName('');
        setDisplayName('');
        alert('File uploaded successfully!');
        fetchFolders();
      } catch (error) {
        console.error('Failed to upload file:', error);
        alert('Failed to upload file. Please try again.');
      }
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>My Drive</h1>
        <button onClick={() => setIsUploadModalOpen(true)} className="upload-button">Upload File</button>
      </header>
      <div className="breadcrumb">
        <span onClick={() => setCurrentPath('')}>root</span>
        {currentPath.split('/').filter(Boolean).map((folder, index) => (
          <span key={index} onClick={() => handleBreadcrumbClick(index)}>
            {' / '}{folder}
          </span>
        ))}
      </div>
      <div className="content-container">
        {loading ? (
          <div className="loading">Loading...</div>
        ) : error ? (
          <div className="error" onClick={fetchFolders}>
            {error} (Click to retry)
          </div>
        ) : (
          <>
            {folders.length === 0 && files.length === 0 ? (
              <div className="empty">This folder is empty</div>
            ) : (
              <>
                {folders.map(folder => (
                  <div
                    key={folder}
                    className="item folder"
                    onClick={() => handleSelect(folder)}
                  >
                    <span className="icon">üìÅ</span>
                    <span className="name">{folder}</span>
                  </div>
                ))}

                {files.map(file => (
                  <div key={file.fileId} className="item file">
                    <span className="icon">üìÑ</span>
                    <span className="name">
                      {file.displayName || file.name}
                    </span>
                    <span className="size">
                      {(file.size / 1024 / 1024).toFixed(2)} MB
                    </span>
                  </div>
                ))}
              </>
            )}
          </>
        )}
      </div>

      <Modal
        isOpen={isUploadModalOpen}
        onRequestClose={() => setIsUploadModalOpen(false)}
        contentLabel="Upload File"
        className="upload-modal"
        overlayClassName="upload-modal-overlay"
      >
        <h2>Upload File to {currentPath || 'root'}</h2>
        <div className="upload-form">
          <div className="form-group">
            <label>File URL:</label>
            <input
              type="text"
              value={uploadUrl}
              onChange={(e) => setUploadUrl(e.target.value)}
              placeholder="Enter file URL"
            />
          </div>
          <div className="form-group">
            <label>Custom File Name:</label>
            <input
              type="text"
              value={customFileName}
              onChange={(e) => setCustomFileName(e.target.value)}
              placeholder="Enter file name"
            />
          </div>
          <div className="form-group">
            <label>Display Name (optional):</label>
            <input
              type="text"
              value={displayName}
              onChange={(e) => setDisplayName(e.target.value)}
              placeholder="Enter display name"
            />
            <small>Use this for multi-word names</small>
          </div>
          <div className="button-group">
            <button onClick={handleUploadFile}>Upload</button>
            <button onClick={() => setIsUploadModalOpen(false)}>Cancel</button>
          </div>
        </div>
      </Modal>
    </div>
  );
}

export default App;
