import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FolderSelector.css';

const B2_CONFIG = {
  keyId: '005eb77b989a2b70000000006',
  applicationKey: 'K005oTpW9P0cUAyBSZPLJsta50Bu0jQ',
  bucketId: '3ebbc707abc9d8399a320b17',
  bucketName: 'plr-assets',
  s3ApiUrl: 'https://s3.us-west-004.backblazeb2.com'
};

function FolderSelector({ onSelect, selectedFolder }) {
  const [folders, setFolders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    fetchFolders();
  }, []);

  const fetchFolders = async () => {
    try {
      setLoading(true);

      // Create AWS-style signature headers
      const dateStamp = new Date().toISOString().split('T')[0];
      const authString = Buffer.from(`${B2_CONFIG.keyId}:${B2_CONFIG.applicationKey}`).toString('base64');

      // List objects using S3-compatible API
      const response = await axios({
        method: 'GET',
        url: `${B2_CONFIG.s3ApiUrl}/${B2_CONFIG.bucketName}`,
        params: {
          'list-type': '2',
          'delimiter': '/'
        },
        headers: {
          'Authorization': `Basic ${authString}`,
          'Date': dateStamp,
          'x-amz-date': dateStamp
        }
      });

      // Parse XML response to get folders
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(response.data, "text/xml");

      // Get both CommonPrefixes (folders) and files that might be in folders
      const commonPrefixes = Array.from(xmlDoc.getElementsByTagName('CommonPrefix')).map(
        prefix => prefix.getElementsByTagName('Prefix')[0].textContent.replace(/\/$/, '')
      );

      const contents = Array.from(xmlDoc.getElementsByTagName('Contents')).map(
        content => {
          const key = content.getElementsByTagName('Key')[0].textContent;
          const folderPath = key.split('/').slice(0, -1).join('/');
          return folderPath;
        }
      ).filter(path => path !== '');

      // Combine and deduplicate folders
      const uniqueFolders = new Set([...commonPrefixes, ...contents]);
      const sortedFolders = Array.from(uniqueFolders).sort();

      setFolders(['root', ...sortedFolders]);
      setError(null);
    } catch (error) {
      console.error('Folder fetch error:', error);
      setError('Failed to fetch folders. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSelect = (folder) => {
    onSelect(folder === 'root' ? '' : folder);
    setIsOpen(false);
  };

  return (
    <div className="folder-selector">
      <button 
        type="button"
        className="folder-selector-trigger"
        onClick={() => setIsOpen(!isOpen)}
      >
        <span>{selectedFolder || 'Select Folder'}</span>
        <span className="arrow">‚ñº</span>
      </button>

      {isOpen && (
        <div className="folder-list">
          {loading ? (
            <div className="folder-item loading">Loading folders...</div>
          ) : error ? (
            <div className="folder-item error" onClick={fetchFolders}>
              {error} (Click to retry)
            </div>
          ) : folders.length === 0 ? (
            <div className="folder-item">No folders found</div>
          ) : (
            folders.map(folder => (
              <div
                key={folder}
                className={`folder-item ${selectedFolder === folder ? 'selected' : ''}`}
                onClick={() => handleSelect(folder)}
              >
                üìÅ {folder || 'root'}
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

export default FolderSelector;
