import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import './App.css';

const API_BASE_URL = process.env.REACT_APP_API_URL;

Modal.setAppElement('#root'); // Set the app element for accessibility

function App() {
  const [folders, setFolders] = useState([]);
  const [currentPath, setCurrentPath] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isUploadModalOpen, setIsUploadModalOpen] = useState(false);
  const [uploadUrl, setUploadUrl] = useState('');

  useEffect(() => {
    fetchFolders();
  }, [currentPath]);

  const fetchFolders = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_BASE_URL}/b2/folders`, {
        params: { path: currentPath }
      });
      setFolders(response.data.folders);
      setError(null);
    } catch (error) {
      console.error('Folder fetch error:', error);
      setError('Failed to fetch folders. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSelect = (folder) => {
    const newPath = folder === 'root' ? '' : `${currentPath}/${folder}`;
    setCurrentPath(newPath);
  };

  const handleBreadcrumbClick = (index) => {
    const newPath = currentPath.split('/').slice(0, index + 1).join('/');
    setCurrentPath(newPath);
  };

  const handleUploadFile = async () => {
    if (uploadUrl.trim()) {
      try {
        const response = await axios.post(`${API_BASE_URL}/download`, {
          fileUrl: uploadUrl,
          folder: currentPath
        });
        console.log(response.data.message);
        setUploadUrl('');
        alert('File uploaded successfully!');
      } catch (error) {
        console.error('Failed to upload file:', error);
        alert('Failed to upload file. Please try again.');
      }
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>My Drive</h1>
        <button onClick={() => setIsUploadModalOpen(true)} className="upload-button">Upload File</button>
      </header>
      <div className="breadcrumb">
        <span onClick={() => setCurrentPath('')}>root</span>
        {currentPath.split('/').filter(Boolean).map((folder, index) => (
          <span key={index} onClick={() => handleBreadcrumbClick(index)}>
            {' / '}{folder}
          </span>
        ))}
      </div>
      <div className="folder-container">
        {loading ? (
          <div className="folder-item loading">Loading folders...</div>
        ) : error ? (
          <div className="folder-item error" onClick={fetchFolders}>
            {error} (Click to retry)
          </div>
        ) : folders.length === 0 ? (
          <div className="folder-item">No folders found</div>
        ) : (
          folders.map(folder => (
            <div
              key={folder}
              className="folder-item"
              onClick={() => handleSelect(folder)}
            >
              üìÅ {folder || 'root'}
            </div>
          ))
        )}
      </div>

      <Modal
        isOpen={isUploadModalOpen}
        onRequestClose={() => setIsUploadModalOpen(false)}
        contentLabel="Upload File"
        className="upload-modal"
        overlayClassName="upload-modal-overlay"
      >
        <h2>Upload File to {currentPath || 'root'}</h2>
        <input
          type="text"
          value={uploadUrl}
          onChange={(e) => setUploadUrl(e.target.value)}
          placeholder="File URL to upload"
        />
        <button onClick={handleUploadFile}>Upload</button>
        <button onClick={() => setIsUploadModalOpen(false)}>Close</button>
      </Modal>
    </div>
  );
}

export default App;
