import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FolderSelector.css';

const B2_CONFIG = {
  keyId: '005eb77b989a2b70000000006',
  applicationKey: 'K005oTpW9P0cUAyBSZPLJsta50Bu0jQ',
  bucketId: '3ebbc707abc9d8399a320b17',
  apiUrl: 'https://api.backblazeb2.com'
};

function FolderSelector({ onSelect, selectedFolder }) {
  const [folders, setFolders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    fetchFolders();
  }, []);

  const fetchFolders = async () => {
    try {
      setLoading(true);

      // First, get authorization token
      const authResponse = await axios({
        method: 'GET',
        url: `${B2_CONFIG.apiUrl}/b2api/v2/b2_authorize_account`,
        headers: {
          Authorization: `Basic ${btoa(`${B2_CONFIG.keyId}:${B2_CONFIG.applicationKey}`)}`
        }
      });

      // Then list files
      const listResponse = await axios({
        method: 'POST',
        url: `${authResponse.data.apiUrl}/b2api/v2/b2_list_file_names`,
        headers: {
          Authorization: authResponse.data.authorizationToken
        },
        data: {
          bucketId: B2_CONFIG.bucketId,
          delimiter: '/',
          prefix: ''
        }
      });

      // Extract unique folders from file paths
      const uniqueFolders = new Set();

      // Add folders from files
      listResponse.data.files.forEach(file => {
        const path = file.fileName;
        const folderPath = path.split('/').slice(0, -1).join('/');
        if (folderPath) uniqueFolders.add(folderPath);
      });

      // Add folders from common prefixes
      if (listResponse.data.commonPrefixes) {
        listResponse.data.commonPrefixes.forEach(prefix => {
          uniqueFolders.add(prefix.replace(/\/$/, '')); // Remove trailing slash
        });
      }

      const sortedFolders = Array.from(uniqueFolders).sort();
      setFolders(['root', ...sortedFolders]);
      setError(null);
    } catch (error) {
      console.error('Folder fetch error:', error);
      setError('Failed to fetch folders. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSelect = (folder) => {
    onSelect(folder === 'root' ? '' : folder);
    setIsOpen(false);
  };

  return (
    <div className="folder-selector">
      <button 
        type="button"
        className="folder-selector-trigger"
        onClick={() => setIsOpen(!isOpen)}
      >
        <span>{selectedFolder || 'Select Folder'}</span>
        <span className="arrow">‚ñº</span>
      </button>

      {isOpen && (
        <div className="folder-list">
          {loading ? (
            <div className="folder-item loading">Loading folders...</div>
          ) : error ? (
            <div className="folder-item error" onClick={fetchFolders}>
              {error} (Click to retry)
            </div>
          ) : folders.length === 0 ? (
            <div className="folder-item">No folders found</div>
          ) : (
            folders.map(folder => (
              <div
                key={folder}
                className={`folder-item ${selectedFolder === folder ? 'selected' : ''}`}
                onClick={() => handleSelect(folder)}
              >
                üìÅ {folder || 'root'}
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

export default FolderSelector;
