import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FolderSelector.css';

// B2 API Configuration
const B2_API = {
  apiUrl: 'https://api.backblazeb2.com',
  credentials: {
    keyId: 'YOUR_B2_KEY_ID',
    applicationKey: 'YOUR_B2_APPLICATION_KEY'
  },
  bucketId: 'YOUR_B2_BUCKET_ID'
};

function FolderSelector({ onSelect, selectedFolder }) {
  const [folders, setFolders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    fetchFolders();
  }, []);

  const fetchFolders = async () => {
    try {
      setLoading(true);

      // Step 1: Get B2 Authorization
      const authResponse = await axios({
        method: 'get',
        url: `${B2_API.apiUrl}/b2api/v3/b2_authorize_account`,
        headers: {
          Authorization: `Basic ${btoa(`${B2_API.credentials.keyId}:${B2_API.credentials.applicationKey}`)}`
        }
      });

      // Step 2: List Files and Folders
      const { data: authData } = authResponse;
      const listResponse = await axios({
        method: 'post',
        url: `${authData.apiUrl}/b2api/v2/b2_list_file_names`,
        headers: {
          Authorization: authData.authorizationToken
        },
        data: {
          bucketId: B2_API.bucketId,
          delimiter: '/',
          prefix: ''
        }
      });

      // Extract folders from response
      const uniqueFolders = new Set();

      // Add folders from file paths
      listResponse.data.files.forEach(file => {
        const pathParts = file.fileName.split('/');
        if (pathParts.length > 1) {
          const folderPath = pathParts.slice(0, -1).join('/');
          uniqueFolders.add(folderPath);
        }
      });

      // Add folders from common prefixes
      if (listResponse.data.commonPrefixes) {
        listResponse.data.commonPrefixes.forEach(prefix => {
          uniqueFolders.add(prefix.replace(/\/$/, ''));
        });
      }

      const sortedFolders = Array.from(uniqueFolders).sort();
      setFolders(['root', ...sortedFolders]);
      setError(null);

    } catch (error) {
      console.error('B2 API Error:', error);
      const errorMessage = error.response?.data?.message 
        || error.response?.data?.code 
        || error.message 
        || 'Failed to fetch folders';
      setError(`Error: ${errorMessage}`);
    } finally {
      setLoading(false);
    }
  };

  const handleSelect = (folder) => {
    onSelect(folder === 'root' ? '' : folder);
    setIsOpen(false);
  };

  return (
    <div className="folder-selector">
      <button 
        type="button"
        className="folder-selector-trigger"
        onClick={() => setIsOpen(!isOpen)}
      >
        <span>{selectedFolder || 'Select Folder'}</span>
        <span className="arrow">‚ñº</span>
      </button>

      {isOpen && (
        <div className="folder-list">
          {loading ? (
            <div className="folder-item loading">Loading folders...</div>
          ) : error ? (
            <div className="folder-item error" onClick={fetchFolders}>
              {error} (Click to retry)
            </div>
          ) : folders.length === 0 ? (
            <div className="folder-item">No folders found</div>
          ) : (
            folders.map(folder => (
              <div
                key={folder}
                className={`folder-item ${selectedFolder === folder ? 'selected' : ''}`}
                onClick={() => handleSelect(folder)}
              >
                üìÅ {folder || 'root'}
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

export default FolderSelector;
